/**css reset**/
* {
    box-sizing: border-box;
}

*:before,
*:after {
    box-sizing: border-box;
}

* {
    margin: 0;
    padding: 0
}

ul,
ol {
    list-style: none;
}

a {
    color: inherit;
    text-decoration: none;
}

image {
    max-width: 100%;
    max-height: 100%;
}

body {
    background: #eee;
    /* background-size: 100% 100%;
    background-image: url(./images/1.jpg); */
}

.globalHeader {
    margin: 40px 20px 40px 20px;
}

@media (min-width:500px) {
    .globalHeader {
        margin: 150px 20px 60px 20px;
    }

}

.searchForm {
    display: flex;
    justify-content: space-between;
}

@media (min-width:500px) {
    .searchForm {
        max-width: 500px;
        margin-left: auto;
        margin-right: auto;
    }

}

.searchForm>input {
    width: 100%;
    margin-right: 10px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
}

.searchForm>button {
    padding: 5px 20px;
    white-space: nowrap;
    background: #0282B9;
    color: #eee;
    border: none;
    border-radius: 4px;
    font-size: 16px;
}

.searchImg {
    width: 29px;
    height: 30px;
}

.globalMain {
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;
}

.siteList {
    margin: 20px;
    display: flex;
    flex-wrap: wrap;

}

/* @media (max-width:500px) {
    .siteList {
        justify-content: space-between;

    }
} */


@media (min-width:500px) {
    .siteList {
        margin-left: 0;
        margin-right: -12.5px;
        justify-content: flex-start;

    }
}

.siteList>li {
    margin-bottom: 10px;
}

@media (min-width:500px) {
    .siteList>li {
        margin-bottom: 10px;
        margin-right: 12.5px;
    }
}

.siteList>li>.site {
    width: 170px;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    background: white;
    border-radius: 4px;
    padding: 20px 0;
    position: relative;
    cursor: pointer;
}

.siteList>li>.site>.close {
    position: absolute;
    right: 5px;
    top: 0;
    display: none;
}

.siteList>li>.site:hover>.close {
    display: block;
}

.siteList>li>.site>.logo {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
}

.siteList>li>.site>.link {
    font-size: 15px;
    margin-top: 4px;
}

.addButton {
    width: 170px;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    background: white;
    border-radius: 4px;
    padding: 20px 0;

}

.addButton>.icon-wrapper {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
}

.addButton>.icon-wrapper>.icon {
    width: 56px;
    height: 56px;

}
